import { defineConfig } from 'vite'
import react from '@vitejs/plugin-react'
import tailwindcss from '@tailwindcss/vite'

export default defineConfig({
  plugins: [react(), tailwindcss()],
})

import js from '@eslint/js'
import globals from 'globals'
import reactHooks from 'eslint-plugin-react-hooks'
import reactRefresh from 'eslint-plugin-react-refresh'
import tseslint from 'typescript-eslint'

export default tseslint.config(
  { ignores: ['dist'] },
  {
    extends: [js.configs.recommended, ...tseslint.configs.recommended],
    files: ['**/*.{ts,tsx}'],
    languageOptions: {
      ecmaVersion: 2020,
      globals: globals.browser,
    },
    plugins: {
      'react-hooks': reactHooks,
      'react-refresh': reactRefresh,
    },
    rules: {
      ...reactHooks.configs.recommended.rules,
      'react-refresh/only-export-components': [
        'warn',
        { allowConstantExport: true },
      ],
    },
  },
)

@import "tailwindcss";

:root {
  --color-primary: #8B7355;
  --color-secondary: #E8DFD3;
  --color-accent: #D4A574;
  --color-background: #FAF9F7;
  --color-text: #2C2C2C;
  --color-text-light: #6B6B6B;
  --font-heading: 'Playfair Display', serif;
  --font-body: 'Inter', sans-serif;
}

@theme {
  --font-family-heading: var(--font-heading);
  --font-family-body: var(--font-body);
}

interface ServiceCardProps {
  title: string
  description: string
  duration: string
  price: string
  icon: string
}

export default function ServiceCard({ title, description, duration, price, icon }: ServiceCardProps) {
  return (
    <div className="bg-white rounded-2xl p-8 shadow-lg hover:shadow-xl transition-shadow duration-300">
      <div className="text-4xl mb-4">{icon}</div>
      <h3 className="font-heading text-2xl text-[#2C2C2C] mb-3">{title}</h3>
      <p className="text-[#6B6B6B] mb-4">{description}</p>
      <div className="flex justify-between items-center text-sm">
        <span className="text-[#8B7355] font-medium">{duration}</span>
        <span className="text-[#2C2C2C] font-semibold">{price}</span>
      </div>
    </div>
  )
}

const timeSlots = [
  '9:00 AM', '9:30 AM', '10:00 AM', '10:30 AM', '11:00 AM', '11:30 AM',
  '12:00 PM', '12:30 PM', '1:00 PM', '1:30 PM', '2:00 PM', '2:30 PM',
  '3:00 PM', '3:30 PM', '4:00 PM', '4:30 PM', '5:00 PM', '5:30 PM'
]

export default function TimeSlotPicker() {
  return (
    <div>
      <label className="block text-[#2C2C2C] font-medium mb-2">
        Available Time Slots
      </label>
      <div className="grid grid-cols-3 sm:grid-cols-4 gap-3">
        {timeSlots.map(slot => (
          <button
            key={slot}
            className="p-2 border border-[#E8DFD3] rounded-lg hover:bg-[#8B7355] hover:text-white transition-colors duration-200 text-sm"
          >
            {slot}
          </button>
        ))}
      </div>
    </div>
  )
}

interface StaffCardProps {
  name: string
  role: string
  specialties: string[]
  experience: string
  image: string
}

export default function StaffCard({ name, role, specialties, experience, image }: StaffCardProps) {
  return (
    <div className="text-center">
      <div className="mb-4 relative overflow-hidden rounded-full w-48 h-48 mx-auto">
        <img 
          src={image} 
          alt={name}
          className="w-full h-full object-cover"
        />
      </div>
      <h3 className="font-heading text-2xl text-[#2C2C2C] mb-1">{name}</h3>
      <p className="text-[#8B7355] font-medium mb-2">{role}</p>
      <p className="text-sm text-[#6B6B6B] mb-3">{experience} experience</p>
      <div className="space-y-1">
        {specialties.map((specialty, index) => (
          <span key={index} className="block text-xs text-[#6B6B6B]">
            {specialty}
          </span>
        ))}
      </div>
    </div>
  )
}

export default function PricingSection() {
  const pricingCategories = [
    {
      category: 'Hair Services',
      services: [
        { name: 'Women\'s Haircut', price: '$65-$85' },
        { name: 'Men\'s Haircut', price: '$45-$55' },
        { name: 'Color Service', price: '$95-$150' },
        { name: 'Highlights/Balayage', price: '$150-$250' },
        { name: 'Hair Treatment', price: '$45-$75' }
      ]
    },
    {
      category: 'Facial Treatments',
      services: [
        { name: 'Classic Facial', price: '$85' },
        { name: 'Anti-Aging Facial', price: '$120' },
        { name: 'Hydrating Facial', price: '$95' },
        { name: 'Chemical Peel', price: '$150' },
        { name: 'Microdermabrasion', price: '$135' }
      ]
    },
    {
      category: 'Nail Services',
      services: [
        { name: 'Classic Manicure', price: '$45' },
        { name: 'Gel Manicure', price: '$65' },
        { name: 'Classic Pedicure', price: '$55' },
        { name: 'Spa Pedicure', price: '$75' },
        { name: 'Nail Art', price: 'From $10' }
      ]
    }
  ]

  return (
    <section className="py-20 px-4 sm:px-6 lg:px-8 bg-[#E8DFD3]/20">
      <div className="max-w-6xl mx-auto">
        <h2 className="font-heading text-4xl sm:text-5xl text-center text-[#2C2C2C] mb-4">
          Our Pricing
        </h2>
        <p className="text-center text-[#6B6B6B] mb-16 max-w-2xl mx-auto">
          Transparent pricing for all our services. Prices may vary based on hair length and complexity
        </p>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {pricingCategories.map((category, index) => (
            <div key={index} className="bg-white rounded-2xl p-8 shadow-lg">
              <h3 className="font-heading text-2xl text-[#8B7355] mb-6">
                {category.category}
              </h3>
              <div className="space-y-4">
                {category.services.map((service, serviceIndex) => (
                  <div key={serviceIndex} className="flex justify-between items-center pb-3 border-b border-[#E8DFD3] last:border-0">
                    <span className="text-[#2C2C2C]">{service.name}</span>
                    <span className="text-[#8B7355] font-medium">{service.price}</span>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  )
}

export default function Gallery() {
  const images = [
    {
      id: 1,
      url: 'https://images.unsplash.com/photo-1560066984-138dadb4c035?w=600&h=600&fit=crop',
      alt: 'Salon interior'
    },
    {
      id: 2,
      url: 'https://images.unsplash.com/photo-1522337360788-8b13dee7a37e?w=600&h=600&fit=crop',
      alt: 'Hair styling'
    },
    {
      id: 3,
      url: 'https://images.unsplash.com/photo-1633681122544-f7e4b26fa1fa?w=600&h=600&fit=crop',
      alt: 'Nail service'
    },
    {
      id: 4,
      url: 'https://images.unsplash.com/photo-1519823551278-64ac92734fb1?w=600&h=600&fit=crop',
      alt: 'Facial treatment'
    },
    {
