interface TestimonialCardProps {
  name: string
  rating: number
  comment: string
  service: string
  date: string
}

export default function TestimonialCard({ name, rating, comment, service, date }: TestimonialCardProps) {
  return (
    <div className="bg-white rounded-2xl p-8 shadow-lg">
      <div className="flex mb-4">
        {[...Array(rating)].map((_, i) => (
          <span key={i} className="text-[#D4A574] text-xl">‚òÖ</span>
        ))}
      </div>
      <p className="text-[#2C2C2C] mb-6 italic">"{comment}"</p>
      <div className="border-t border-[#E8DFD3] pt-4">
        <p className="font-medium text-[#2C2C2C]">{name}</p>
        <p className="text-sm text-[#6B6B6B]">{service} ‚Ä¢ {date}</p>
      </div>
    </div>
  )
}
